<template>
	<div id="user-index">
		<el-container class="all-screen" direction="vertical">
			<el-header height="50px">
				<div class="dots" style="display:inline-block;">
					<i
						class="iconfont icon-yuandianda"
						style="color:#dd6d60;font-size:25px;padding-right:5px;"
					></i>
					<i
						class="iconfont icon-yuandianda"
						style="color:#aed292;font-size:25px;"
					></i>
				</div>
				<el-input
					placeholder=""
					v-model="searchInput"
					prefix-icon="el-icon-search"
					clearable
					@keyup.enter="searchSongs"
					@clear="clearSearchInput"
				></el-input>
				<!-- style -->
				<el-dropdown
					style="position: absolute !important;right: 40px;top: 10px;color: #fff !important;width:120px;height: 30px;line-height: 30px;"
				>
					<button
						class="user-settings"
						style="border-radius:15px;padding:1px;width:100%;height:30px;"
					>
						<!-- <i
							class="el-icon-user-solid"
							style="margin-right: 5px"
						></i> -->
						<img
							src="../assets/imgs/user-image.jpg"
							alt=""
							srcset=""
							style="display: inline-block;width:30px;height:30px;margin-right: 5px;border-radius:15px;"
						/>
						<span>
							{{ name }}
						</span>
					</button>
					<template #dropdown>
						<el-dropdown-menu>
							<el-dropdown-item
								@click="refresh"
							>

							</el-dropdown-item>
							<el-dropdown-item
								@click="changePwDialog"
							>

							</el-dropdown-item>
              <el-dropdown-item
                  @click="changeIdDialog"
              >

              </el-dropdown-item>
              <el-dropdown-item @click="showPayDialog = true">

              </el-dropdown-item>
							<el-dropdown-item @click="logOut">

							</el-dropdown-item>
						</el-dropdown-menu>
					</template>
				</el-dropdown>
			</el-header>
			<el-container>
				<el-aside width="200px">
					<el-menu
						default-active="1"
						class="el-menu-vertical-music"
						text-color="#fff"
						background-color="transparent"
						active-text-color="#f56c6c"
						router="true"
					>
						<el-menu-item route="/discover" index="1">
							<!-- <router-link to="/main/discover">
								<i class="iconfont icon-discover"></i>
								<template #title></template>
							</router-link> -->
							<i
								class="iconfont icon-discover"
								style="padding-right:10px;"
							></i>
							<template #title></template>
						</el-menu-item>
						<el-menu-item route="/hotmusic" index="2">
							<i
								class="iconfont icon-new-music"
								style="padding-right:10px;"
							></i>
							<template #title></template>
						</el-menu-item>
						<el-menu-item route="/recommendsongs" index="3">
							<i
								class="iconfont icon-musiclist"
								style="padding-right:10px;"
							></i>
							<template #title></template>
						</el-menu-item>
						<el-menu-item route="/recommendusers" index="4">
							<i
								class="iconfont icon-userlist"
								style="padding-right:10px;"
							></i>
							<template #title></template>
						</el-menu-item>
						<el-menu-item route="/loveSong" index="5">
							<i
								class="iconfont icon-shoucang_huaban1"
								style="padding-right:10px;"
							></i>
							<template #title></template>
						</el-menu-item>
					</el-menu>
					<!-- </el-col>

					</el-row> -->
				</el-aside>

				<el-container style="border-radius:5px;position:relative;">
					<!-- height:0;flex-grow:1;el-mainel-footer -->
					<el-main
						style="background-color:#fff;padding:10px;height:0;flex-grow:1;border-radius:5px;position:relative;"
					>
						<!-- router-viewplaymusic -->
						<router-view @playmusic="changMP3Url"></router-view>
					</el-main>
					<el-footer
						style="background-color:rgb(241,243,244)"
						height="50px"
					>
						<div>
							<div class="song-info">
								<img
									:src="picUrl"
									alt=""
									style="display: inline-block;width:50px;height:50px;"
								/>
								<div>
									<span
										style="display:inline-block;width:100px;overflow:hidden;font-size:14px;"
									>
										{{ songName }}
									</span>
									<span
										style="display:inline-block;width:100px;overflow:hidden;font-size:14px;color:#9b9b9b;"
									>
										{{ "-  " + singerName }}
									</span>
								</div>

								<!-- <div></div> -->
							</div>
							<audio
								id="audio"
								:src="MP3Url"
								controls="controls"
								loop="loop"
                controlsList="nodownload"
								@timeUpdate="audioTimeUpdate"
								style="width:1100px;height:50px;right: 0;position: absolute;"
							></audio>
						</div>
					</el-footer>
				</el-container>
			</el-container>
		</el-container>
		<!--  -->
		<el-dialog
			title=""
			modal="false"
			v-model="userinfoDialogVisibleEdit"
		>
			<el-form :model="form">
				<el-form-item label="id" :label-width="formLabelWidth">
					<el-input
						v-model="uid"
						autocomplete="off"
						disabled="true"
					></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input v-model="name" autocomplete="off"></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input v-model="gender" autocomplete="off"></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input v-model="age" autocomplete="off"></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input v-model="area" autocomplete="off"></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input
						v-model="registerTime"
						autocomplete="off"
						disabled="true"
					></el-input>
				</el-form-item>
        <el-form-item label="" :label-width="formLabelWidth">
          <el-input
              v-model="bal"
              autocomplete="off"
              disabled="true"
          ></el-input>
        </el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input v-model="des" autocomplete="off"></el-input>
				</el-form-item>
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input
						v-model="password"
						autocomplete="off"
						disabled="true"
						style="width:550px"
						:type="pwType"
					></el-input>
					<i
						style="margin-left:10px;cursor:pointer;"
						:class="[
							'iconfont',
							'icon-mimaxianshi',
							pwType == 'password' ? 'hide' : 'display',
						]"
						@click="changePwType"
					></i>
				</el-form-item>
			</el-form>
			<template #footer>
				<span class="dialog-footer">
					<el-button type="primary" @click="updateUser">

					</el-button>
					<el-button @click="userinfoDialogVisibleEdit = false">

					</el-button>
				</span>
			</template>
		</el-dialog>
		<!--  -->
		<el-dialog title="" modal="false" v-model="pwDialogVisibleEdit">
			<el-form :model="form">
				<el-form-item label="" :label-width="formLabelWidth">
					<el-input
						clearable
						v-model="oldPassword"
						autocomplete="off"
						placeholder=""
						style="width:550px"
						:type="pwType"
					></el-input>
					<i
						style="margin-left:10px;cursor:pointer;"
						class="iconfont icon-mimaxianshi"
						@click="changePwType"
					></i>
				</el-form-item>
				<el-form-item label=" " :label-width="formLabelWidth">
					<el-input
						clearable
						v-model="newPassword"
						autocomplete="off"
						placeholder=""
						style="width:550px"
						:type="pwType"
					></el-input>
					<i
						style="margin-left:10px;cursor:pointer;"
						class="iconfont icon-mimaxianshi"
						@click="changePwType"
					></i>
				</el-form-item>
				<el-form-item label=" " :label-width="formLabelWidth">
					<el-input
						clearable
						v-model="repeatNewPassword"
						autocomplete="off"
						placeholder=""
						style="width:550px"
						:type="pwType"
					></el-input>
					<i
						style="margin-left:10px;cursor:pointer;"
						class="iconfont icon-mimaxianshi"
						@click="changePwType"
					></i>
				</el-form-item>
			</el-form>
			<template #footer>
				<span class="dialog-footer">
					<el-button type="primary" @click="updatePw">

					</el-button>
					<el-button @click="pwDialogVisibleEdit = false">

					</el-button>
				</span>
			</template>
		</el-dialog>

    <!--  -->
    <el-dialog title="" modal="false" v-model="idDialog">
      <el-form>
        <el-form-item label="" :label-width="formLabelWidth">
        <el-upload
            ref="upload"
            action="api/baidu/idRecognize"
            :show-file-list=false
            :file-list="fileList"
            list-type="picture-card"
            :on-success="handleSuccess">
<!--          <i slot="default" class="el-icon-plus"></i>-->
          <img v-if="imageUrl" :src="imageUrl" style="width:300px; height:160px">
          <i v-else class="el-icon-plus avatar-uploader-icon"></i>
        </el-upload>

        </el-form-item>
        <el-form-item label="" :label-width="formLabelWidth">
          <el-input clearable v-model="idno" autocomplete="off" disabled="true"
          ></el-input>
        </el-form-item>
      </el-form>
      <template #footer>
				<span class="dialog-footer">
					<el-button type="primary" @click="idConfirm">

					</el-button>
					<el-button @click="idDialog = false">

					</el-button>
				</span>
      </template>
    </el-dialog>

    <el-dialog title="" modal="false" v-model="showPayDialog">
      <el-form>
        <el-form-item label="" :label-width="formLabelWidth">
          <el-input clearable v-model="amount" autocomplete="off" ></el-input>
        </el-form-item>
      </el-form>
      <template #footer>
				<span class="dialog-footer">
					<el-button type="primary" @click="pay">

					</el-button>
					<el-button @click="showPayDialog = false">

					</el-button>
				</span>
      </template>
    </el-dialog>
	</div>
</template>
<script>
import { ref } from "vue"
// 
import { useStore } from "vuex"
// 
import { _updataPw, _updataUser, _addRecord } from "../network/request"
import {idconfirm, refresh, play} from "../network/user";
import {add, pay} from "../network/order";

export default {
	name: "main",
	created() {
		const user = useStore().state.user
		console.log(useStore().state)
		// state
		this.state = useStore().state
		// 
		this.uid = user.uid
    this.id = user.id
		this.gender = user.gender
		this.age = user.age
		this.area = user.area
		this.des = user.des
		this.registerTime = user.registerTime
		this.name = user.name
		this.password = user.password
    this.idno = user.idno
    this.phone = user.phone
    this.bal = user.bal
		console.log("" + user.name)

		// 
		if (window.sessionStorage.getItem("user")) {
			console.log("")
			const session_user = JSON.parse(
				window.sessionStorage.getItem("user"),
			)
			console.log(session_user)
			this.$store.commit("SET_USER", user)
		}
		// 
		window.addEventListener("beforeunload", () => {
			console.log("")
			console.log(this.$store.state.user)
			window.sessionStorage.setItem(
				"user",
				JSON.stringify(this.$store.state.user),
			)
		})
	},
	data() {
		return {
      amount: 0.0,
      showPayDialog:false,
			// 
			state: useStore().state,
			// 
			// uid
      id: 0,
			uid: ref("uid"),
			// 
			name: ref(""),
			gender: ref(""),
			age: ref(""),
			area: ref(""),
			registerTime: ref(""),
			des: ref(""),
			password: ref(""),
			oldPassword: ref(""),
			newPassword: ref(""),
			repeatNewPassword: ref(""),
      imageUrl:'',
      idno:"",
      phone:"",
      bal:0.0,
			// 

			// id
			iid: "",
      fileList:[],
			// 
			searchInput: ref(""),
			// 
			songName: ref(""),
			singerName: ref(""),
			MP3Url: ref(
				"http://m10.music.126.net/20210601101449/f548450009d6fd68ac324f1fff77570c/ymusic/f189/538f/29a0/aa78032d24c11eb57283eb577a26a6dd.mp3",
			),
			picUrl: ref(
				"https://p1.music.126.net/KZ0VfIoFYsxpjz9sTQuLVQ==/17687843556430013.jpg",
			),
			// 

			// 
			audioIsPlaying: false,
			// 
			userinfoDialogVisibleEdit: false,
			// 
			pwDialogVisibleEdit: false,
      idDialog: false,
			// 
			formLabelWidth: "120px",
			// 
			isAddRecord: false,
			// 
			pwType: "password",
		}
	},

	methods: {
    refresh(){
      refresh({'id':this.id}).then(res=>{
        // console.log(res.data.data);
        this.bal = res.data.data.bal
        this.userinfoDialogVisibleEdit = true
      })
    },
    pay(){
      add({'user_id':this.id,'amount':this.amount,'reason':''}).then(res=>{
        const orderId = res.data.data.id
        this.showPayDialog = false
        // console.log(res);
        pay(orderId).then(res=>{
          console.log(res)
          const win = window.open('')
          win.document.open()
          win.document.write(res.data)
          win.document.close()
        })
      })
    },
    idConfirm(){
      // console.log(this.uid);
      // console.log(this.idno);
      idconfirm({'uid':this.uid,'idno':this.idno}).then(res=>{
        // console.log(res);
        this.$message({type: "success", message: res.data.message,})
        this.idDialog = false;
      })
    }
    ,
    handleSuccess(file, res){
      // console.log(file)
      // let obj = { name: file.fileName, url: file.fileDownloadUri }
      console.log(res.response.data)
      this.imageUrl = res.response.data.fileDownloadUri;
      this.idno = res.response.data.words;
      // console.log(res.response.message);
      // this.picList.push(obj)
      // this.list = res.response.data
    },
		// 
		clearChangePwForm() {
			this.oldPassword = ref("")
			this.newPassword = ref("")
			this.repeatNewPassword = ref("")
		},
		// 
		updateInfo() {
			const user = this.state.user
			user.name = this.name
			user.area = this.area
			user.des = this.des
			user.age = this.age
			user.gender = this.gender
			console.log(user)
			this.$store.commit("SET_USER", user)
		},
		// audio
		autoPlay() {
			const audio = document.getElementById("audio")
			audio.play()
		},
		//  audio
		closePlay() {
			const audio = document.getElementById("audio")
			audio.pause()
			audio.load()
		},
		// MP3UrlMP3Urlaudio
		changMP3Url(
			valueIid,
			valueMP3Url,
			valuePicUrl,
			valueSongName,
			valueSingerName,
		) {
			// changMP3Url(valueMP3Url) {
      play() //
			console.log("")
			this.iid = valueIid
      console.log(this.iid)
			console.log(valueMP3Url)
			this.MP3Url = valueMP3Url
			console.log(valuePicUrl)
			this.picUrl = valuePicUrl
			console.log(valueSongName)
			this.songName = valueSongName
			console.log(valueSingerName)
			this.singerName = valueSingerName
			console.log("")
			// 
			console.log("")
			this.closePlay()
			console.log("")
			this.autoPlay()
			// false
			this.isAddRecord = false
		},
		// 
		logOut() {
			this.$confirm("?", "", {
				confirmButtonText: "",
				cancelButtonText: "",
				type: "warning",
			})
				.then(() => {
					this.$message({
						type: "success",
						message: "!",
					})
					// 
					window.sessionStorage.clear()
					//   
					this.$router.push("/login")
				})

				.catch(() => {
					this.$message({
						type: "info",
						message: "",
					})
				})
		},
		// 
		updateUser() {
			this.$confirm(", ?", "", {
				confirmButtonText: "",
				cancelButtonText: "",
				type: "warning",
			})
				.then(() => {
					_updataUser({
						uid: this.uid,
						name: this.name,
						gender: this.gender,
						age: this.age,
						area: this.area,
						des: this.des,
					}).then((response) => {
						if (response.data.code == 200) {
							this.$message({
								type: "success",
								message: "!",
							})
							this.userinfoDialogVisibleEdit = false
							// 
							this.updateInfo()
						} else {
							this.$message({
								type: "info",
								message: "!",
							})
							this.userinfoDialogVisibleEdit = false
						}
					})
				})
				.catch(() => {
					this.$message({
						type: "info",
						message: "",
					})
				})
		},
		// 
		changePwDialog() {
      this.pwDialogVisibleEdit = true
			// console.log("")
			// 
			this.clearChangePwForm()
		},
    changeIdDialog() {
      this.idno = ''
      this.idDialog = true
    },
		// 
		updatePw() {
			this.$confirm(", ?", "", {
				confirmButtonText: "",
				cancelButtonText: "",
				type: "warning",
			})
				.then(() => {
					console.log(this.oldPassword == "")
					if (
						this.oldPassword == "" ||
						this.newPassword == "" ||
						this.repeatNewPassword == ""
					) {
						console.log("")
						this.$message({
							type: "warning",
							message: "",
						})
						// 
						this.pwDialogVisibleEdit = false
						return false
					}
					if (this.newPassword === this.repeatNewPassword) {
						_updataPw({
							uid: this.uid,
							oldPw: this.oldPassword,
							newPw: this.newPassword,
						}).then((response) => {
							console.log(response.data)
							if (response.data.code == 200) {
								this.$message({
									type: "success",
									message: "!",
								})
								this.password = this.newPassword
								console.log("")
								this.pwDialogVisibleEdit = false
							} else {
								this.$message({
									type: "warning",
									message: "!",
								})
								console.log("")
								this.pwDialogVisibleEdit = false
							}
						})
					} else {
						this.$message({
							type: "warning",
							message: "!",
						})
					}
				})
				.catch(() => {
					this.$message({
						type: "info",
						message: "",
					})
				})
		},
		// 
		searchSongs() {
			console.log("" + this.searchInput)
			console.log("")
			//
			this.$router.push({
				path: "/searchMusic",
				query: {
					keyword: this.searchInput,
				},
			})
		},
		// discover
		clearSearchInput() {
			console.log("")
			this.$router.push("/discover")
		},
		// 30
		audioTimeUpdate() {
			console.log("")
			var audio = document.getElementById("audio")
			var currTime = audio.currentTime
			console.log(currTime)
			// 30
			if (!this.isAddRecord && currTime > 30) {
				console.log("")
				_addRecord({
					uid: this.uid,
					iid: this.iid,
				}).then((response) => {
					console.log(response.data.data)
					if (response.data.code == 200) {
						console.log("")
						// 
						this.isAddRecord = true
					}
				})
			}
		},
		// 
		changePwType() {
			console.log("")
			if (this.pwType == "password") {
				console.log("")
				this.pwType = "name"
			} else {
				console.log("")
				this.pwType = "password"
			}
		},
	},
}
</script>

<style scoped src="../assets/css/main.css">
/* csssrc */

/*  */
#user-index >>> .display {
	cursor: pointer;
	color: #f56c6c;
}
#user-index >>> .hide {
	cursor: pointer;
}

.avatar {
  width: 100px;
  height: 60px;
}
</style>
